sys_data_source.parsing_script:
// The input value can be accessed through the variables named "line", "lineNumber" and "result"
// The function uses result variable to return parse result back. 
(function(line, lineNumber, result) {

    var map = {};
	if (global.JSUtil.notNil(line)) {
    var resultLine = line.replace("  ", " ");
    var items = resultLine.split(" ");
		//downloaded payload from agent log on platform
		/*
		if (global.JSUtil.notNil(items[0]) && global.JSUtil.notNil(items[1]) ) {
    map['datetime'] = items[0] + " " + items[1];
	}
		if (global.JSUtil.notNil(items[2]) ) {
    map['level'] = items[2];
		}
    for (var k = 3; k < 30; k++) {
        if (global.JSUtil.notNil(items[k])) {
            if (global.JSUtil.notNil(map['message'])) {
                map['message'] += items[k] + " ";
            } else {
                map['message'] = items[k] + " ";
            }
        }
    }
		*/
		//for agent log downloaded directly from midserver
			if (global.JSUtil.notNil(items[0]) ) {
    map['datetime'] = items[0] ;
	}
		if (global.JSUtil.notNil(items[1]) ) {
    map['level'] = items[1];
		}
    for (var k = 2; k < 30; k++) {
        if (global.JSUtil.notNil(items[k])) {
            if (global.JSUtil.notNil(map['message'])) {
                map['message'] += items[k] + " ";
            } else {
                map['message'] = items[k] + " ";
            }
        }
    }
    result.addRow(map);
	}

})(line, lineNumber, result);
